fid = fopen('sigma_err_0.txt', 'r');format_spec = '%f %d';sizeA = [2 Inf]A = fscanf(fid, format_spec,sizeA);fclose(fid);A = A';y = A(:,1);x = A(:,2);fid = fopen('sigma_err_1.txt', 'r');format_spec = '%f %d';sizeA = [2 Inf]A = fscanf(fid, format_spec,sizeA);fclose(fid);A = A';u = A(:,1);v = A(:,2);fid = fopen('sigma_err_10.txt', 'r');format_spec = '%f %d';sizeA = [2 Inf]A = fscanf(fid, format_spec,sizeA);fclose(fid);A = A';z = A(:,1);q = A(:,2);loglog(x,y);hold onloglog(v,u);hold onloglog(q,z);title('estimation of sigma with mu = 0');legend('sigma = 0.1', 'sigma = 1','sigma = 10');ylabel('estimation error');xlabel('N');